// src/app/main/main.component.ts
import { Component } from '@angular/core';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.sass']
})
export class MainComponent {
  locked: string | null = null;

  constructor() {
    // guard for SSR
    if (typeof window !== 'undefined') {
      console.log(window);
    }
  }

  checkout(ev: CustomEvent | Event): void {
    console.log('in the checkout method ', (ev as CustomEvent).detail);
    console.log('the ev object ', ev);

    this.locked = localStorage.getItem('__unlockProtocol.locked');
    const account = localStorage.getItem('__unlockProtocol.accountAddress');

    console.log('locked: ', this.locked);
    console.log('account address: ', account);

    if (typeof window !== 'undefined' && (window as any).unlockProtocol) {
      // (window as any).unlockProtocol.loadCheckoutModal();
    }
  }
}
