import { Component, OnInit } from '@angular/core';
import { AuthService } from '../core/auth.service';

@Component({
  selector: 'app-d-tracker',
  templateUrl: './d-tracker.component.html',
  styleUrls: ['./d-tracker.component.sass']
})
export class DTrackerComponent {
  currmonth = 'Feb';
  constructor(public auth: AuthService) { }

}
<nav class="level" *ngIf="auth.user | async as user">
  <div class="level-item has-text-centered">
    <div>
      <p class="heading">{{currmonth}} Allocated Desires</p>
      <p class="title">{{user.desiresTotal | currency:'USD':true:'1.2'}}</p>
    </div>
  </div>
</nav>
