import { Component } from '@angular/core';
import { AuthService } from '../core/auth.service';
@Component({
  selector: 'app-user-profile',
  templateUrl: './user-profile.component.html',
  styleUrls: ['./user-profile.component.sass']
})
export class UserProfileComponent {

  constructor(public auth: AuthService) { }

  onPayment (payment) {
    console.log('on payment: ', payment);
  }

  onError (error) {
    console.log('there was an error: ', error);
  }


}
<div *ngIf="auth.user | async; then authenticated else guest">
</div>

<ng-template #guest>
  <button class="button navbar-item" (click)="auth.googleLogin()">Login</button>
</ng-template>

<ng-template #authenticated>
  <div class="navbar-item" *ngIf="auth.user | async as user">
    <!-- <p class="button navbar-item"></p> -->
    <button class="button navbar-item" (click)="auth.logout()"> Logout</button>
    <!-- <button class="button navbar-item" (click)="auth.logout()"> {{ user.displayName }} Logout</button> -->
  </div>
</ng-template>
